murmurhash_flag, findstart_flag, copy_flag, crc32_flag, rc4_flag, security_init_flag, warzone_vscode_start_flag = False, False, False,False,False,True,False

file_with_match = []
file_without_match = []

files = list(os.listdir(folder))
files.sort()
for file in files:
    with open(folder+'/'+file, "rb") as f:
        data = f.read()
        found_murmurhash = False
        found_findstart = False
        found_copy = False
        found_crc32 = False
        found_rc4 = False
        found_security_init = False
        if murmurhash_flag:
            for byte in bytes_murmurhash:
                if byte in data:
                    file_with_match.append(file+':'+str(bytes_murmurhash.index(byte)+1)+' murmurhash')
                    found_murmurhash = True
                    # break
            if not found_murmurhash:
                file_without_match.append(file + ': murmurhash')
                print(f'{file} - murmurhash')
        if findstart_flag:
            for byte in bytes_findstart:
                if byte in data:
                    file_with_match.append(file+':'+str(bytes_findstart.index(byte)+1)+' findstart')
                    found_findstart = True
                    # break
            if not found_findstart:
                file_without_match.append(file + ': findstart')
                print(f'{file} - findstart')
        if copy_flag:
            for byte in bytes_copy:
                if byte in data:
                    file_with_match.append(file+':'+str(bytes_copy.index(byte)+1)+' copy')
                    found_copy = True
                    # break
            if not found_copy:
                file_without_match.append(file + ': copy')
                print(f'{file} - copy')
        if crc32_flag:
            for i,byte in enumerate(crc32):
                if byte in data:
                    file_with_match.append(file+': crc32_'+str(i+1))
                    found_crc32 = True
            if not found_crc32:
                file_without_match.append(file + ': crc32')
                print(f'{file} - crc32')
        if rc4_flag:
            for i,byte in enumerate(rc4):
                if byte in data:
                    file_with_match.append(file+': rc4_'+str(i+1))
                    found_rc4 = True
            if not found_rc4:
                file_without_match.append(file + ': rc4')
                print(f'{file} - rc4')
        if security_init_flag:
            for i,byte in enumerate(security_init):
                if byte in data:
                    file_with_match.append(file+': '+ str(security_init.index(byte)+1) +' security_init')
                    found_security_init = True
            if not found_security_init:
                file_without_match.append(file + ': security_init')
                print(f'{file} - security_init')
        if warzone_vscode_start_flag:
            for i,byte in enumerate(warzone_vscode_start):
                if byte in data:
                    file_with_match.append(file+':'+ str(warzone_vscode_start.index(byte)+1) +' warzone_vscode_start')
                    found_warzone_vscode_start = True
            if not found_warzone_vscode_start:
                file_without_match.append(file + ': warzone_vscode_start')
                print(f'{file} - warzone_vscode_start')
with open(output_file, 'w') as f:
    f.write('Files with match:\n')
    for file in file_with_match:
        f.write(file+'\n')
    f.write('\nFiles without match:\n')
    for file in file_without_match:
        f.write(file+'\n')
        